mapper.select:
select count(1)
from pms_attribute


        and id = #{id}


        and template_id = #{templateId}


        and name = #{name}


        and description = #{description}


        and type = #{type}


        and input_type = #{inputType}


        and value_list = #{valueList}


        and unit = #{unit}


        and sort = #{sort}


        and is_allow_customize = #{isAllowCustomize}


        and gmt_create = #{gmtCreate}


        and gmt_modified = #{gmtModified}

select count(*)
from pms_attribute
where name = #{name}
  and template_id = #{templateId}

select count(*)
from pms_attribute
where template_id = #{templateId}

mapper.delete:
delete
from pms_attribute
where id in(

    #{id}

)
